import{_ as i,o,c as t,U as l}from"./chunks/framework.e0394e94.js";const u=JSON.parse('{"title":"小程序生命周期","description":"","frontmatter":{},"headers":[],"relativePath":"Interview/JingJiang/MiniProgram/小程序生命周期.md","filePath":"Interview/JingJiang/MiniProgram/小程序生命周期.md","lastUpdated":1689411224000}'),n={name:"Interview/JingJiang/MiniProgram/小程序生命周期.md"},e=l('<h1 id="小程序生命周期" tabindex="-1">小程序生命周期 <a class="header-anchor" href="#小程序生命周期" aria-label="Permalink to &quot;小程序生命周期&quot;">​</a></h1><p>应用生命周期（共7个）：后期做小程序时，onLaunch 用的最多（比如获取openId）</p><ul><li><strong>onLaunch</strong>生命周期回调——监听小程序初始化。</li><li><strong>onShow</strong>生命周期回调——监听小程序启动或切前台。</li><li><strong>onHide</strong>生命周期回调——监听小程序切后台。</li></ul><p>页面生命周期（共14个）：onLoad用的最多 option 可以拿到路由传递的参数</p><ul><li><strong>onLoad</strong>生命周期回调—监听页面加载</li><li><strong>onShow</strong>生命周期回调—监听页面显示</li><li><strong>onReady</strong>生命周期回调—监听页面初次渲染完成</li><li><strong>onHide</strong>生命周期回调—监听页面隐藏</li><li><strong>onUnload</strong>生命周期回调—监听页面卸载</li></ul><p>组件生命周期：</p><ol><li>组件自身生命周期（共6个）：</li></ol><ul><li><strong>created</strong>在组件实例刚刚被创建时执行</li><li><strong>attached</strong>在组件实例进入页面节点树时执行</li><li><strong>detached</strong>在组件实例被从页面节点树移除时执行</li></ul><div class="tip custom-block"><p class="custom-block-title">TIP</p><ul><li>created 生命周期回调—监听页面加载</li><li>attached 生命周期回调—监听页面显示</li><li>ready 生命周期回调—监听页面初次渲染完成</li><li>moved 生命周期回调—监听页面隐藏</li><li>detached 生命周期回调—监听页面卸载</li><li>error 每当组件方法抛出错误时执行</li></ul></div><ol start="2"><li>组件所在页面的生命周期函数（共3个）</li></ol><ul><li>show组件所在的页面被展示时执行</li><li>hide组件所在的页面被隐藏时执行</li><li>resize组件所在的页面尺寸变化时执行</li></ul>',11),r=[e];function a(s,d,c,_,g,p){return o(),t("div",null,r)}const m=i(n,[["render",a]]);export{u as __pageData,m as default};
